  return (
    <article className="cart-item">
      <img src={image} alt={title} />
      <div>
        <h4>{title}</h4>
        <h5>${price}</h5>
        <button type="button" className="cart-btn remove-btn"
        onClick={() => {
          console.log("item removed");

        }}>
          Remove
        </button>
      </div>
    </article>
